    private void uploadImageBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_uploadImageBTNActionPerformed
        //To allow VD upload the image from the file explorer
        JFileChooser saveFileChooser = new JFileChooser();
        saveFileChooser.setFileFilter(new FileNameExtensionFilter("JPEG Images", "jpg"));
        
        //Only allow ".png" extension for choosing the image file
        int returnValue = saveFileChooser.showSaveDialog(null);
        
        //If file is selected
        if (returnValue == JFileChooser.APPROVE_OPTION){
            //Get the selected file path
            File f = saveFileChooser.getSelectedFile();
            filePath = f.getAbsolutePath();
            if (filePath!=null) {
                String[] file = filePath.split("\\.");
                String fileExtension = file[1];
                
                if (fileExtension.equals(this.defaultFileExtension)) {
                    try {
                        //Display the image in the jLabel
                        setImageIcon(filePath);
                    } catch (IOException ex) {
                        Logger.getLogger(VendorProfile.class.getName()).log(Level.SEVERE, null, ex);
                    }
                }
                else {
                    profilePicLabel.setText("Invalid File Selected");
                }
            }
        }
    }//GEN-LAST:event_uploadImageBTNActionPerformed

    private void updateBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateBTNActionPerformed
        // TODO add your handling code here:
        String userID = rsdIdTF.getText();
        String unitNo = unitNoTF.getText();
        String email = emailTF.getText();
        String password = String.valueOf(passwordTF.getPassword());
        String firstName = firstNameTF.getText();
        String lastName = lastNameTF.getText();
        String idNo = icTF.getText();
        String gender = String.valueOf(genderCB.getSelectedItem());
        String phoneNo = contactTF.getText();
        
        Validation vd = new Validation();
        if(vd.emailDuplication(email, "userProfile.txt", userID)) {
            if(vd.passwordValid(password)) {
                warningMessage.setText("");
                int result = JOptionPane.showConfirmDialog(null,"Are you sure to "
                        + "modify your personal profile?",
                        "UPDATE PERSONAL PROFILE",
                JOptionPane.YES_NO_OPTION,
                JOptionPane.QUESTION_MESSAGE);

                if(result == JOptionPane.YES_OPTION){
                    Vendor user_temp = new Vendor(userID, email, password, firstName, lastName, idNo, gender, phoneNo, unitNo);
                    user_temp.modifySelfAccount();
                    VD = user_temp;
                    
                    JOptionPane.showMessageDialog (null, "Personal profile has been updated!", 
                                    "UPDATE PERSONAL PROFILE", JOptionPane.INFORMATION_MESSAGE);
                    dispose();
                    VD.toViewProfile(VD);
                }
            } else {
                warningMessage.setText("Invalid password!");
            }
        } else {
            warningMessage.setText("Email is taken!");
        }
    }//GEN-LAST:event_updateBTNActionPerformed

